import React, { Component } from 'react';
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';

import {fetchBulkData,fetchStudentFile} from './../util/api';

const XLSX = require('xlsx')
export default class History extends Component{
    constructor(props){
        super(props)
        this.state={
            disable:false,
            metaData:[],
            studentData:[],
        }
        
    }
async loadData(){
       const response = await fetchBulkData().then((jsondata) => {
            var metaData=[]
            let result = JSON.parse(jsondata.data);
            var arrData = {}
            console.log("Bulk Meta Data : ",result)
            result[0].forEach((element ,id)=> {
                arrData=element
                // console.log(element)
                metaData=[
                    ...metaData,
                    {
                        "regId":arrData.regId,
                        "uploadFileName": arrData.uploadFileName,
                        "centerName": arrData.centerName,
                        "records":arrData.records,
                        "uploadDate":arrData.uploadDate
                    }]
            });
           this.setState({metaData})
            console.log(this.state.metaData)
        })
    }

    // Download the data
    downloadFile(e,x){
        let studentData=[]
            const regId= e
            const file_name = x
            // console.log(x)
            var data={}
            fetchStudentFile(regId).then((jsondata)=>{
                let res = JSON.parse(jsondata.data)
                res[0].forEach(element=>{
                    console.log(element)
                    data= element  
                    studentData=[
                        ...studentData,{
                            "Reg Id":data.regId,
                            "First Name":data.firstName,
                            "Middle Name": data.middleName,
                            "Last Name": data.lastName,
                            "DOB": data.dob,
                            "Aadhar Number": data.aadharNo,

                        }]
                })
                // console.log("Student data in bulk : ",res)
                const workSheet = XLSX.utils.json_to_sheet(res)
                const workBook = XLSX.utils.book_new()

                XLSX.utils.book_append_sheet(workBook,workSheet,file_name)
// Genrarting buffer if there is large amount of data
                XLSX.write(workBook,{bookType:"xlsx",type:"buffer"})

                // Binary String
                XLSX.write(workBook,{bookType:"xlsx",type:"binary"})

                const x = XLSX.writeFile(workBook,file_name)
                console.log("File : ",x)
            })
    }
    render(){
        return(
            <div style={{ width: '100%' }}>

            <Grid container  justify="flex-end" alignItems="flex-end">

             <Button type="submit" color="primary"  onClick={this.loadData.bind(this)}>
               Click to Load Data
             </Button>
             </Grid>

            <Grid>
             <Table aria-label="simple table"  style={{ width: '80%'}}>
            <TableHead>
              <TableRow>
                <TableCell>Reg Id</TableCell>
                <TableCell>File Name</TableCell>
                <TableCell>Center Name</TableCell>
                <TableCell>Records</TableCell>
                <TableCell>Upload Date</TableCell>

              </TableRow>
            </TableHead>
            <TableBody>
            {this.state.metaData.map((dataValue, id) => (
                    <TableRow id="tablerow">
                      <TableCell>{dataValue.regId}</TableCell>
                      <TableCell>{dataValue.uploadFileName}</TableCell>
                      <TableCell>{dataValue.centerName}</TableCell>
                      <TableCell>{dataValue.records}</TableCell>
                      <TableCell>{dataValue.uploadDate}</TableCell>
                      <TableCell>{<Button variant="contained" color="primary" onClick={() => this.downloadFile(dataValue.regId,dataValue.uploadFileName)} > Download </Button>}</TableCell>
                    </TableRow>))}
            </TableBody>
           </Table>

           </Grid>
            </div>
        )
    }
}
