import React, { Component } from 'react';
import * as XLSX from "xlsx";
import {  InputLabel, Input, Grid, Button } from '@material-ui/core';
import { SingleSelect } from "react-select-material-ui";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableRow from '@material-ui/core/TableRow';
import UserContext from '../components/GolbalContext'
import {fetchNotCompletedBatchDetails,saveBulkMetaData,saveBulkStudentData} from '../util/api';
import Student_Template from "./../assets/document/Student_Template.xlsx";

let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
today = yyyy + '-' + mm + '-' + dd;

export default class bulkUpload extends Component{
  constructor(props){
    super(props)
    this.state={

      options:[{value:"Mobilization", label:"Mobilization"},
                {value:"Enrollment" ,label:"Enrollment"}],
      user_unique_id:0,
      batchList:[],
      Selectedoption:"",
      student_count:0,
      fileName:"",
      studentInfo:[],
      batchName:null,
      batchId:0,
      flag : false,
      upload_button:true,
      errors: {},
      batchName:"",
      Aadhar_Number:0,
    }
  }
 
// optind change handler
  handleOptionsChange(selectname,event){
    console.log("model_id: ",UserContext.modelId)
    const value = event
    // console.log(value)
    this.setState({
      Selectedoption: value,
      batchName:"",
      batchId:0,
      errors:{
        ...this.state.errors,
        SelectedBatch: {
          'label': '',
          'value': false
        },
      }
    })
    this.setState({batchList:[]})

    if(event === "Enrollment"){
      this.setState({
        errors:{
          ...this.state.errors,
          SelectedBatch: {
            'label': 'Please select some option',
            'value': true
          },
        }
      })
      // setDisabled(false)
      fetchNotCompletedBatchDetails(UserContext.centerId).then((jsondata) => {
        let result = JSON.parse(jsondata.data);
        // console.log("center : ",result)
       result.forEach(element => {
        //  console.log("Condition for date " , element.freezeDate > today)
          if(element.freezeDate > today ){
            this.state.batchList.push({
              "value": element.batchId,
              "label": element.batchName
            })
          }
       });
      })
    }
    else{
      // setDisabled(true)
    }
  }
   // Hhandle batch changes
   handleBatchChange(selectname,event){
    // console.log("selected optins : ",this.state.Selectedoption)
    let value = event
    // console.log(event)
    let index = this.state.batchList.findIndex((object) => object.value == value)
    let batchname = this.state.batchList[index].label
    // console.log(this.state.batcheList)
    this.setState({
      batchId:event,
      batchName: batchname,
      errors:{
        ...this.state.errors,
        SelectedBatch: {
          'label': '',
          'value': false
        },
      }
    })
  }

  // Handle for excel file change
  readExcel = (file) => {
    // 
    let file_name = file.name
    this.setState({fileName :file_name })
   const fileReader = new FileReader();
   fileReader.readAsArrayBuffer(file);

   fileReader.onload = (e) => {
    //  console.log(e)
     const bufferArray = e.target.result;

     const wb = XLSX.read(bufferArray, { type: "buffer" });

     const wsname = wb.SheetNames[0];

     const ws = wb.Sheets[wsname];
    
    // console.log("file name : ",file.name)
     const data = XLSX.utils.sheet_to_json(ws,{header:1});
    //  console.log("Header file ",data[0])
     let student_data = XLSX.utils.sheet_to_json(ws);
     this.setState({studentInfo:student_data })
    
    
    this.setState({student_count :data.length-1})
    //  console.log("DataCount : ",this.state.student_count )

    const requiredHeader =['Aadhar_Number', 'First_Name', 'Middle_Name', 'Last_Name', 'DOB', 'Gender', 'Highest_Qualification', 'Religion', 'Passing_Year', 'Address_Line_1', 'Village', 'District', 'State', 'PINCODE', 'College_register_no', 'Iti_grade', 'Iti_trade', 'Category', 'Primary_Contact_Number', 'Primary_Mail_ID', 'Created_date', 'Created_by', 'Updated_date', 'Updated_by', 'Student_ID', 'Eng_ID', 'Status', 'Reason', 'Remarks']
    let flag = true
    // var x= requiredHeader.findIndex(element =>{
    //   element == "Created_by"
    // })
    // console.log(x)
    // console.log(data[0] )
    requiredHeader.map((element,id) =>{
      // console.log("header comparison ",element, "data : ",data[0][id])
    
      if(flag){
        if(element==data[0][id]){
            if(id+1 == requiredHeader.length)
            {     
              // Activating the upload button
              this.setState({upload_button:false})      
            }
      }
        else{
          flag = false
           
          alert("please fill the all column as per sample template")
          this.setState({upload_button:true})
          return;
        }
      }
    })
   }
}
// Uploadinh the doumnet

 async uploadDocuments(){
  let x = 0
  // alert("hello")
  // console.log(UserContext.modelId)
  // console.log(this.state.student_count)
  // console.log(this.state.studentInfo)
  // console.log(this.state.fileName)
    const response = await saveBulkMetaData(this.state.fileName,today,this.state.Selectedoption,this.state.batchName,this.state.batchId,this.state.student_count,"P",UserContext.centerName,UserContext.centerId,UserContext.modelId, UserContext.userid,"under test").then((jsondata)=>{
      let result = JSON.parse(jsondata.data)
      // console.log("Mpdel id : ",result[0].modelId)
      // 
      if(result[0].regId ==0){
        alert("Data Not saved")
       // changes
        return;
      }

      // Changes at 20 october
     else{
      this.state.user_unique_id = result[0].regId
      this.uploadStudentData();
     }
    })
    
  }
     
 async uploadStudentData(){
   let flag = false
  let studentInfo =  this.state.studentInfo

  // console.log("Bulk Student data : ",studentInfo)
  console.log(this.state.student_count)
  let x = -1
    for(var i=0;i<this.state.student_count;i++){
      if(studentInfo[i].Aadhar_Number ===undefined || studentInfo[i].DOB=== undefined || studentInfo[i].PINCODE===undefined|| studentInfo[i].Passing_Year===undefined){
        alert("Plaese Fill Aadhar Number, DOB,Pin code and Passing Year of Student "+ studentInfo[i].First_Name)
        x++;
        if(x ===this.state.student_count ){
          for(var i=0;i<this.state.student_count;i++){
          const response1 = await saveBulkStudentData(this.state.user_unique_id,
            studentInfo[i].First_Name,
             studentInfo[i].Last_Name || null,
             studentInfo[i].Middle_Name || null,
             studentInfo[i].DOB,
             studentInfo[i].Aadhar_Number || null,
             studentInfo[i].Highest_Qualification || null,
             studentInfo[i].Passing_Year,
             studentInfo[i].Gender || null,
