import React from 'react';
import * as XLSX from "xlsx";
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import { SingleSelect } from "react-select-material-ui";
import label from 'material-ui/svg-icons/action/label';
import { useState } from "react";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import {DropzoneArea} from 'material-ui-dropzone';
import UserContext from '../components/GolbalContext'
import {fetchNotCompletedBatchDetails} from '../util/api';

let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
today = yyyy + '-' + mm + '-' + dd;

export default function(props){
    const option=[{value:1, label:"Mobilization"},{value:2 ,label:"Enrollment"}]
    
    const [dbUserId,setdbUserId]= useState(0);
    const [engagementId,setengagementId]= useState(0);
    
    if(props.id != null && props.id != undefined)
    {
      setdbUserId=props.id;
      setengagementId=props.engagementId;
    }
    const [batches, setbatches]= useState([]);
    const [selectedOption, setSelectedOption] = useState("")
    const [selectedBatches, setselectedBatches] = useState("")
    const [items, setItems] = useState([]);
    // // Reading excel file

    // const readExcel = (file) => {
    //   const promise = new Promise((resolve, reject) => {
    //     const fileReader = new FileReader();
    //     fileReader.readAsArrayBuffer(file);
  
    //     fileReader.onload = (e) => {
    //       const bufferArray = e.target.result;
  
    //       const wb = XLSX.read(bufferArray, { type: "buffer" });
  
    //       const wsname = wb.SheetNames[0];
  
    //       const ws = wb.Sheets[wsname];
  
    //       const data = XLSX.utils.sheet_to_json(ws);
  
    //       resolve(data);
    //     };
  
    //     fileReader.onerror = (error) => {
    //       reject(error);
    //     };
    //   });
  
    //   promise.then((d) => {
    //     console.log(d)
    //     setItems(d);
    //   });
    // };

    const[error,setserros]= useState("")
    // For handling the dropdown
    const handleChange=((e)=>{
      
      setSelectedOption(e);
      setbatches([])
      setselectedBatches("")
      if(e== 2 || e === "Enrollment"){
        fetchNotCompletedBatchDetails(UserContext.centerId).then((jsondata) => {
          let result = JSON.parse(jsondata.data);
          // console.log(result)
          // console.log(result[0].freezeDate)
         result.forEach(element => {

           console.log("Condition for date " , element.freezeDate > today)
            
            if(element.freezeDate > today ){
              batches.push({
                "value" : element.batchId,
                "label": element.batchName
              })

          }
         });
         console.log(batches)
        })
      }
    })

    // for handling batches changes
   const  batchesChanges= ((e)=>{
      setselectedBatches(e)
      console.log("Batchec : ",e)
      // console.log(UserContext.)
      console.log(setengagementId.label)
      // console.log(selectedBatches.e)
    })

    
    // handlingError((e)=>{
    //   if(setSelectedOption===""){
    //     setserros="Plase select Some option"
    //   }
    // })

    // Handler for file upload
    const onFileChangeHandler = ((file)=>{
      setItems(file);
      console.log(file)
    })

    // Upload document
    const uploadDocuments = ((e)=>{
      
        e.preventDefault();
        items.map(function(setItems){
        // uploadDocument(setItems);
        {uploadDocument(setItems)}
        console.log("file data : ",setItems)
      });
    
    //   this.setState({documentNumber:''});
    //   this.setState({clearDropzoneArea: this.state.clearDropzoneArea + 1});
    //  // this.setState({typeOfDocument:null});
    //   this.setState({ documentType:[]});
        
    })

    // Uploading the file in s3 buckket
    const uploadDocument = ((setItems)=>{
        //   e.preventDefault();
       // var genericDocuments=["8thCertificate","10thCertificate","12thCertificate","StudentPicture","PostGraduation","Graduation"];
       var levelOfDocument="G";
       // if(genericDocuments.includes(this.state.typeOfDocument)){levelOfDocument="G";}
       
       
       let localThis=this;
       
       let reader = new FileReader();
       reader.readAsDataURL(setItems);
       reader.onload = function () {
       let  document = reader.result;
       console.log(document)
      //  uploadDocument(localThis.state.dbUserId,localThis.state.engagementId,documentType,levelOfDocument,localThis.state.typeOfDocument,document,localThis.state.documentNumber)
      //  .then((jsondata) => {
      //        alertDialogOptions.message=<span style={{color:"green"}}>{documentType} File uploaded successfully</span>;
      //        localThis.setState({alertDialogFlag:true});
      //        localThis.getDocuments();
      //     })
       }
         
    })
        return(
          
            <Grid container>
               <Grid container>
                <Grid item xs={12}> </Grid>
                <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Options</InputLabel>
            <SingleSelect
              name="" id=""
              // key={this.state.Selectedstate || ''}
              options={option}
              value={selectedOption}
              onChange={handleChange}
              // error={handlingError}
              // onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              // helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              // error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>

           <Grid item xs={12} sm={2}></Grid>
            <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Batches</InputLabel>
            <SingleSelect
              // key={this.state.Selectedstate || ''}
              name="batchname"  id ="batchId"
              options={batches}
              value={selectedBatches}
              onChange={batchesChanges}
              // onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              // helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              // error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>
          </Grid>
         
          <Table  aria-label="simple table" style={{ width:"100%"}}>
          <TableBody>
          <TableRow>
            <TableCell>   
            <DropzoneArea  
              name="file" id="file"
              maxFileSize={10000000} filesLimit={1} showFileNames={true} 
              onChange={onFileChangeHandler}
              // key={this.state.clearDropzoneArea}
              />
              <p  style={{color:"red"}}>Note-: Document size should not be greater than 10mb.</p>
            </TableCell>
            </TableRow>
            </TableBody>
          </Table>
            <br /> <br />
            <form onSubmit={uploadDocuments} method="post">
              <Grid container direction="row" justify="flex-end" alignItems="flex-end">
              <Button type="submit" variant="contained" color="primary" size="small" 
                    // disabled={this.state.file==null || this.state.typeOfDocument=='' || this.state.documentType==''?true:false}
             >Upload Document</Button>
             </Grid>

             </form>
            </Grid>
       
        )
}
