import excel from 'xlsx';
let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
today = yyyy + '-' + mm + '-' + dd;

export default function(props){
  let student_data_count;
  let selectedOption;
  let batchId
  let batchName;
    const [option]= useState([{value:"Mobilization", label:"Mobilization"},
                                {value:"Enrollment" ,label:"Enrollment"}])

    const[selectedOption1, setSelectedOption1] = useState("Mobilized")
 
    const [disabled,setDisabled]= useState(true); 
    const [batchelist,setBatchelist] = useState([])
    const [batchId1, setBatchId1] = useState("");
    // const [batches, setbatches]= useState("");
    const [selectedBatches, setselectedBatches] = useState([])
    const [items, setItems] = useState("");
    const[error,setserros]= useState("")
    // // Reading excel file

    // Calling the above function to read the file

    const readExcel = (file) => {
        // 
        const file_name = file.name
       const fileReader = new FileReader();
       fileReader.readAsArrayBuffer(file);
 
       fileReader.onload = (e) => {
         console.log(e)
         const bufferArray = e.target.result;
 
         const wb = XLSX.read(bufferArray, { type: "buffer" });
 
         const wsname = wb.SheetNames[0];
 
         const ws = wb.Sheets[wsname];
        
        // console.log("file name : ",file.name)
         const data = XLSX.utils.sheet_to_json(ws,{header:1});

         console.log("Header file ",data)
         const student_data = XLSX.utils.sheet_to_json(ws);
        //  console.log(" Studeny data : ",student_data)
        //  console.log(" Student aadhar : ",student_data[0].Aadhar_Number)
        
        student_data_count = data.length-1
         console.log("DataCount : ",student_data_count )

          const requiredHeader =['Reg_No', 'Aadhar_Number', 'First_Name', 'Middle_Name', 'Last_Name', 'DOB', 'Gender', 'Highest_Qualification', 'Religion', 'Passing_Year', 'Address_Line_1', 'Village', 'District', 'State', 'PINCODE', 'Category', 'Primary_Contact_Number', 'Primary_Mail_ID', 'Created_date', 'Created_by', 'Updated_date', 'Updated_by', 'Student_ID', 'Eng_ID', 'Status', 'Reason']
        let flag = true

        // var x= requiredHeader.findIndex(element =>{
        //   element == "Created_by"
        // })
        // console.log(x)
        console.log(data[0] )
        requiredHeader.map((element,id) =>{
          // console.log("header comparison ",element, "data : ",data[0][id])
        
          if(flag){
            if(element==data[0][id]){
                if(id+1 == requiredHeader.length)
                {
                // alert("Data in proper format");
                setDisabled(false)
                uploadDocuments(file_name,student_data,student_data_count)                
            }
          }
            else{
              flag = false
              alert("please fill the all column as per sample template")
              return;
            }
          }
          else{
            // console.log("please fill the all column should not be repwted twice") 
          }
         
        })
       }
     
   }


    
    // For handling the dropdown
    const handleChange=((e)=>{  
      console.log("before value  ",selectedOption1)
      console.log("Bstch id ", e) 
      setSelectedOption1(e)
      console.log("after value  ",selectedOption1)
      // const select = e.target.value
      // const optionsselected = option.filter((d)=>d.id == select)[0]
      // console.log("id" , optionsselected)

    //  console.log(selectedOption,"  selectedOption")
      if(e === "Enrollment"){
        setDisabled(false)
        fetchNotCompletedBatchDetails(UserContext.centerId).then((jsondata) => {
          let result = JSON.parse(jsondata.data);
          console.log("senter : ",result)
          // console.log(result[0].freezeDate)
          // setbatches([])
         result.forEach(element => {

           console.log("Condition for date " , element.freezeDate > today)
            
            if(element.freezeDate > today ){
              batchelist.push({
                value: element.batchId,
                label: element.batchName
              })
          }
         });
         console.log(batchelist)
        })
      }
      else{
        setDisabled(true)
      }
    })

    // for handling batches changes
   const  batchesChanges= ((e)=>{
      // console.log("BatchecId : ",e)
     
      setBatchId1 (e);
    //  let index = batchelist.findIndex((object) => object.value ==batchId )
    //  batchName = batchelist[index].label
     console.log(batchId1)
    })

    
    // handlingError((e)=>{
    //   if(setSelectedOption===""){
    //     setserros="Plase select Some option"
    //   }
    // })

    // 
    // Upload document
    const uploadDocuments = ((file_name,student_data,student_data_count)=>{
      console.log(student_data_count)
      console.log(student_data)
      console.log(file_name)
        saveBulkMetaData(file_name,today,selectedOption,batchName,batchId,student_data_count,"P",UserContext.centerName,UserContext.centerId,"under test").then((jsondata)=>{
          let result = JSON.parse(jsondata.data)
          console.log(result[0].regId)
          const user_unique_id = result[0].regId

          for(var i=0;i<student_data_count;i++){
            saveBulkStudentData(user_unique_id,student_data[i].First_Name,
              student_data[i].Last_Name,
              student_data[i].Middle_Name,
              student_data[i].DOB,
              student_data[i].Aadhar_Number,
              student_data[i].Highest_Qualification,
              student_data[i].Passing_Year,
              student_data[i].Gender,
              student_data[i].Religion,
              student_data[i].Category,
              student_data[i].Primary_Contact_Number,
              student_data[i].Primary_Mail_ID,

              student_data[i].Address_Line_1,
              student_data[i].Village,

              student_data[i].District,
              student_data[i].State,
              student_data[i].PINCODE,
            
              today,
              selectedOption,
              UserContext.userid,
              "P",
              "Sample creation by us").then((jsondata)=>{
              let result = JSON.parse(jsondata.data)
              console.log(result)
            })
            
          }
        })
    })

    // Uploading the file in s3 buckket
    const uploadDocument = ((setItems)=>{
        //   e.preventDefault();
       // var genericDocuments=["8thCertificate","10thCertificate","12thCertificate","StudentPicture","PostGraduation","Graduation"];
       var levelOfDocument="G";
       // if(genericDocuments.includes(this.state.typeOfDocument)){levelOfDocument="G";}
       
       
       let localThis=this;
       
       let reader = new FileReader();
       reader.readAsDataURL(setItems);
       reader.onload = function () {
       let  document = reader.result;
       console.log(document)
      //  uploadDocument(localThis.state.dbUserId,localThis.state.engagementId,documentType,levelOfDocument,localThis.state.typeOfDocument,document,localThis.state.documentNumber)
      //  .then((jsondata) => {
      //        alertDialogOptions.message=<span style={{color:"green"}}>{documentType} File uploaded successfully</span>;
      //        localThis.setState({alertDialogFlag:true});
      //        localThis.getDocuments();
      //     })
       }
         
    })

// Routes handling for history
    const routeChange=(()=>{
    //  <Route>

    //  </Route>
      
    })
        return(
          
