               name="upload-photo"
               type="file"
               accept='.xlsx/*, .xls/*'
               onClick={e => {
                (e.target.value = null)
                setDisabled(true)
              }
               }
               onChange={(e) => {
                const file = e.target.files[0];
                var substr1 = file.name.substring(file.name.length-4, file.name.length);
                var substr2 = file.name.substring(file.name.length-3, file.name.length);
                if(substr1== "xlsx" || substr2 == "xls"){
                  readExcel(file); 
                }
                else{
                  alert("Please Upload Excel file")
                  return;
                }
               
              }}
              maxFileSize={2000000} filesLimit={1} showFileNames={true}
              />
