import React, { Component } from 'react';
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import { fetchAllStateDetails,fetchStateByZone, fetchCenterByStateName, fetchEmployerDetails, fetchBatchDetails, fetchEnrollmentDetailsByBatchId, getBatchDetailsByBatchIdandFunderId, saveBatchFunderDetails, updateBatchFunderDetails, getBatchDetailsByBatchId } from './../util/api';
import UserContext from '../components/GolbalContext'
import { SingleSelect } from "react-select-material-ui";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import Typography from '@material-ui/core/Typography';
import Box from '@material-ui/core/Box';

let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
let hours = String(today.getHours()).padStart(2, '0');
let minutes = String(today.getMinutes()).padStart(2, '0');
let seconds = String(today.getSeconds()).padStart(2, '0');
today = yyyy + '-' + mm + '-' + dd + ' ' + hours + ':' + minutes + ':' + seconds;
//columns for the table
const columns = [{ label: 'Funder Name', name: 'funderName', headerStyle: { color: '#FF9800' } },
{ label: 'Batch Name', name: 'BatchName', headerStyle: { color: '#FF9800' } },
{ label: 'Start Date', name: 'startDate', headerStyle: { color: '#FF9800' } },
{ label: 'End Date', name: 'endDate', headerStyle: { color: '#FF9800' } },
{ label: 'Map/ Unmap', name: 'button', headerStyle: { color: '#FF9800' } },
{ label: 'No of Trainees', name: 'trainees', headerStyle: { color: '#FF9800' } }
];
//options for the table

export default class FundManeger extends Component {
 
  constructor(props) {
    super(props)
    this.state = {
      regions: [{ value: '1', label: 'All' },
      { value: '2', label: 'East' },
      { value: '3', label: 'West' },
      { value: '4', label: 'North' },
      { value: '5', label: 'South' }],
      statelist: [],
      centerlist: [],
      centerId: "",
      funderId: "",
      funderType: "P",
      funderName: "",
      accountStatus: "Active Partner",
      typeOfRelationship: "%Funding partner%",
      funderlist: [],
      errors: {},
      totalTrainees: 0,
      mappedTableData: [],
      unmappedTableData: [],
      centerName: '',
    };
    this.arrayTableData = this.arrayTableData.bind(this);
    this.sepratingTableData = this.sepratingTableData.bind(this);
    this.saveAll = this.saveAll.bind(this);
    this.updateAll = this.updateAll.bind(this);
    this.checkforPrimaryFunderStatus = this.checkforPrimaryFunderStatus.bind(this);
  }

  // Module drop down for regions
  handleRegionsChange(selectname, event) {
    this.setState({
      [selectname]: event,
      errors: {
        ...this.state.errors,
        regions: {
          'label': '',
          'value': false
        },
        Selectedstate: {
          'label': 'Please select some option',
          'value': true
        },
        centerId: {
          'label': 'Please select some option',
          'value': true
        },
        funderId: {
          'label': 'Please select some option',
          'value': true
        }
      },
      Selectedstate: '',
      statelist: [],
      centerId: '',
      centerlist: [],
      funderId: '',
      funderlist: [],
      mappedTableData: [],
      unmappedTableData: []
    })
    fetchStateByZone(event).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      console.log(result)
      // result.forEach(element => {
      //   this.state.statelist.push({
      //     "value": element.name,
      //     "label": element.name
      //   })
      // });
    })
  }

  // drop down for state
  handleStateChange(selectname, event) {
    this.setState({
      [selectname]: event,
      errors: {
        ...this.state.errors,
        Selectedstate: {
          'label': '',
          'value': false
        },
        centerId: {
          'label': 'Please select some option',
          'value': true
        },
        funderId: {
          'label': 'Please select some option',
          'value': true
        }
      },
      centerId: '',
      centerlist: [],
      funderId: '',
      funderlist: [],
      mappedTableData: [],
      unmappedTableData: []

    })
    fetchCenterByStateName(event).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      result.forEach(element => {
        this.state.centerlist.push({
          "value": element.id,
          "label": element.name,
        })
      })
    })
  }

  // drop down for center name
  handleCenterChange(selectname, event) {
    this.setState({
      [selectname]: event,
      errors: {
        ...this.state.errors,
        centerId: {
          'label': '',
          'value': false
        },
        funderId: {
          'label': 'Please select some option',
          'value': true
        }
      },
      funderId: '',
      funderlist: [],
      mappedTableData: [],
      unmappedTableData: []
    })
    // Employer api calling to fetch funderlist
    fetchEmployerDetails(this.state.accountStatus, this.state.typeOfRelationship).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      console.log(result)
      result.forEach(element => {
        this.state.funderlist.push({
          "value": element.id,
          "label": element.accountName
        })
      })
    })
  }

  // drop down for funder list
  handleFunderChange(selectname, event) {
    let funderId = event;
    let index = this.state.funderlist.findIndex((object) => object.value == funderId)
    let funderName = this.state.funderlist[index].label
    this.setState({
      [selectname]: event,
      errors: {
        ...this.state.errors,
        funderId: {
          'label': '',
          'value': false
        }
      },
      funderName,
      mappedTableData: [],
      unmappedTableData: []
    })
    this.arrayTableData(funderId, funderName)
  }

  handleRadioChange(event) {
    let value = event.target.value
    this.setState({ funderType: value });
    this.arrayTableData(this.state.funderId, this.state.funderName)
  }

  // Center id is static i.e 20 for a time being
  arrayTableData(funderId, funderName) {
    var tableData = []
    fetchBatchDetails(this.state.centerId).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      var arrData = {}
      result.map((element, id) => {
        arrData = element
        tableData = [
          ...tableData,
          {
            "BatchName": arrData.batchName,
            "batchId": arrData.batchId,
            "funderName": funderName,
            "startDate": arrData.startDate,
            "endDate": arrData.endDate,
            "funderId": funderId,
            "trainees": 0,
            "id": "",
            "isActive": "Y",
            "fundingType": this.state.funderType,
            "remarks": "",
          }]
      })
      this.sepratingTableData(tableData);
    })
  }

  sepratingTableData(tableData) {
    console.log("Sperating Table Data begins")
    this.setState({ mappedTableData: [], unmappedTableData: [], totalTrainees: 0 })
    var mappedTableData = [];
    var unmappedTableData = [];
    var totalTrainees = 0;
    tableData.map((element, id) => {
      //to get the student Strength present based on the enrollment ID's 
      fetchEnrollmentDetailsByBatchId(element.batchId).then((jsondata) => {
        let res = JSON.parse(jsondata.data)
        tableData[id].trainees = res.length
      })
      getBatchDetailsByBatchIdandFunderId(element.batchId, element.funderId, "Y", this.state.funderType).then((jsondata) => {
        let res = JSON.parse(jsondata.data)
        if (res[0].length > 0) {
          tableData[id].id = res[0][0].id
          tableData[id].isActive = res[0][0].isActive
          tableData[id].fundingType = res[0][0].fundingType
          tableData[id].remarks = res[0][0].remarks
          tableData[id].mapped = true
          totalTrainees += res.length
          mappedTableData = [
            ...mappedTableData,
            tableData[id]
          ]
        }
        else  {
          let flagIfPresent = this.checkforPrimaryFunderStatus(element.batchId)
          console.log("flag If Present outside ===>  ",this.checkforPrimaryFunderStatus(element.batchId), flagIfPresent)
          if((this.state.funderType!="P" && flagIfPresent) || (this.state.funderType == "S")){
            unmappedTableData = [
              ...unmappedTableData,
              tableData[id]
            ]
          }      
        }
        this.setState({ unmappedTableData, mappedTableData, totalTrainees })
      })
    })
  }

  checkforPrimaryFunderStatus(batchId){
    getBatchDetailsByBatchId(batchId, "Y", this.state.funderType).then((jsondata)=>{
      let res = JSON.parse(jsondata.data)
      console.log(res[0])
      let flagIfPresent = res[0].length > 0 ? false : true
      console.log("flag If Present inside ===>  ",flagIfPresent)
      return flagIfPresent
    })
  }

  //save batch funder mapping 
  saveFunderMappingDetails(batchId, funderId, isActive, fundingType, createdBy, createdOn, remarks, isAll) {
    saveBatchFunderDetails(batchId, funderId, isActive, fundingType, createdBy, createdOn, remarks).then((jsondata) => {
      let res = JSON.parse(jsondata.data)
      if (res[0].length > 0) {
        console.log('data saved successfully')
      }
    })
    if (!isAll) {
      this.arrayTableData(this.state.funderId, this.state.funderName)
    }
  }

  //update batch funder mapping 
  updateFunderMappingDetails(id, batchId, funderId, isActive, remarks, updatedOn, updatedBy, fundingType, isAll) {
    updateBatchFunderDetails(id, batchId, funderId, isActive, remarks, updatedOn, updatedBy, fundingType).then((jsondata) => {
      let res = JSON.parse(jsondata.data)
      if (res[0].length > 0) {
        console.log('data updated successfully')
      }
    })
    if (!isAll) {
      this.arrayTableData(this.state.funderId, this.state.funderName)
    }
  }

  saveAll() {
    this.state.unmappedTableData.map((element, id) => {
      this.saveFunderMappingDetails(element.batchId, element.funderId, element.isActive, this.state.funderType, UserContext.userid, today, "", true)
    })
    this.arrayTableData(this.state.funderId, this.state.funderName)
  }

  updateAll() {
    this.state.mappedTableData.map((element, id) => {
      this.updateFunderMappingDetails(element.id, element.batchId, element.funderId, "N", "", today, UserContext.userid, this.state.funderType, true)
    })
    this.arrayTableData(this.state.funderId, this.state.funderName)
  }

  render() {

    return (
      <div style={{ width: '100%' }}>
        <Grid container>
          <Grid item xs={12}> </Grid>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Regions</InputLabel>
            <SingleSelect
              name="Selectedregion" id="Selectedregion"
              options={this.state.regions}
              value={this.state.Selectedregion || ''}
              key={this.state.Selectedregion || ''}
              onChange={this.handleRegionsChange.bind(this, 'Selectedregion')}
              helperText={this.state.errors.Selectedregion != undefined ? this.state.errors.Selectedregion.label : ''}
              error={this.state.errors.Selectedregion != undefined ? this.state.errors.Selectedregion.value : ''}
            />
          </Grid>

          <Grid item xs={12} sm={2}></Grid>

          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >State</InputLabel>
            <SingleSelect
              name="Selectedstate" id="Selectedstate"
              key={this.state.Selectedstate || ''}
              options={this.state.statelist}
              value={this.state.Selectedstate || ''}
              onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>
        </Grid>

        <br />

        <Grid container>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Center Name</InputLabel>
            <SingleSelect
              name="selectedcenter" id="centerId"
              options={this.state.centerlist}
              value={this.state.centerId || ''}
              onChange={this.handleCenterChange.bind(this, 'centerId')}
              helperText={this.state.errors.centerId != undefined ? this.state.errors.centerId.label : ''}
              error={this.state.errors.centerId != undefined ? this.state.errors.centerId.value : ''}
              key={this.state.centerId || ''}
            />
          </Grid>

          <Grid item xs={12} sm={2}></Grid>

          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Funding Partner</InputLabel>
            <SingleSelect
              name="selectedfunder" id="funderId"
              options={this.state.funderlist}
              value={this.state.funderId || ''}
              onChange={this.handleFunderChange.bind(this, 'funderId')}
              helperText={this.state.errors.funderId != undefined ? this.state.errors.funderId.label : ''}
              error={this.state.errors.funderId != undefined ? this.state.errors.funderId.value : ''}
              key={this.state.funderId || ''}
            />
          </Grid>

        </Grid>
        <br />
        <br />
        <Grid container>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Funding Type</InputLabel>
            <br />
            <div onChange={this.handleRadioChange.bind(this)}>
              <input type="radio" value="P" name="funderType" checked={this.state.funderType === "P"} /> Primary
              &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <input type="radio" value="S" name="funderType" checked={this.state.funderType === "S"} /> Supporting
            </div>
          </Grid>

          <Grid item xs={12} sm={2}></Grid>

          <Grid item xs={12} sm={2}>
          <Typography variant="body1" id="tableTitle" component="div"> Total Mapped Students </Typography>
            <br />
            {this.state.totalTrainees}
          </Grid>
        </Grid>

        <br /><br /><br /><br />
        <Grid container>
          <Grid item xs={12} sm={5}>
            <Typography variant="body1" id="tableTitle" component="div"> UnMapped Batches </Typography>
            <Box border={1} borderRadius="1.75%">
              <Table aria-label="simple table">

                <TableHead>
                  <TableRow>
                    <TableCell>Batch Name</TableCell>
                    <TableCell>Start Date</TableCell>
                    <TableCell>End Date</TableCell>
                    <TableCell>Trainees</TableCell>
                  </TableRow>
                </TableHead>

                <TableBody>
                  {this.state.unmappedTableData.map((dataValue, id) => (
                    <TableRow id="tablerow">
                      <TableCell>{dataValue.BatchName}</TableCell>
                      <TableCell>{dataValue.startDate}</TableCell>
                      <TableCell>{dataValue.endDate}</TableCell>
                      <TableCell>{dataValue.trainees}</TableCell>
                      <TableCell>{<Button variant="contained" color="primary" onClick={() => this.saveFunderMappingDetails(dataValue.batchId, dataValue.funderId, "Y", this.state.funderType, UserContext.userid, today, '', false)} > map </Button>}</TableCell>
                    </TableRow>))}
                </TableBody>

              </ Table>
            </Box>
          </Grid>

          <Grid item xs={12} sm={1}></Grid>

          <Grid item xs={12} sm={5}>
            <Typography variant="body1" id="tableTitle" component="div"> Mapped Batches </Typography>

            <Box border={1} borderRadius="1.75%">
              <Table aria-label="simple table">

                <TableHead>
                  <TableRow>
                    <TableCell>Batch Name</TableCell>
                    <TableCell>Start Date</TableCell>
                    <TableCell>End Date</TableCell>
                    <TableCell>Trainees</TableCell>
                  </TableRow>
                </TableHead>

                <TableBody>
                  {this.state.mappedTableData.map((dataValue, id) => (
                    <TableRow id="tablerow">
                      <TableCell>{dataValue.BatchName}</TableCell>
                      <TableCell>{dataValue.startDate}</TableCell>
                      <TableCell>{dataValue.endDate}</TableCell>
                      <TableCell>{dataValue.trainees}</TableCell>
                      <TableCell>{<Button variant="contained" color="primary" onClick={() => this.updateFunderMappingDetails(dataValue.id, dataValue.batchId, dataValue.funderId, "N", '', today, UserContext.userid, this.state.funderType, false)} > unmap </Button>}</TableCell>
                    </TableRow>))}
                </TableBody>

              </ Table>
            </Box>
          </Grid>

        </Grid>

        <br /> <br />
        <Grid container>
          <Grid item xs={6}>
            <Button variant="contained" color="primary" disabled={(this.state.unmappedTableData == [] || this.state.unmappedTableData == undefined || this.state.unmappedTableData == "") ? true : false} onClick={this.saveAll}>Map All</Button>
          </Grid>
          <Grid item xs={6}>
            <Button variant="contained" color="primary" disabled={(this.state.mappedTableData == [] || this.state.mappedTableData == undefined || this.state.mappedTableData == "") ? true : false} onClick={this.updateAll}>Unmap All</Button>
          </Grid>
        </Grid>
        <br /><br />
      </div>
    )
  }
}
