import React from 'react';
import * as XLSX from "xlsx";
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import { SingleSelect } from "react-select-material-ui";
import label from 'material-ui/svg-icons/action/label';
import { useState } from "react";
export default function(){
    const option=[{value:1, label:"Mobilization"},{value:2 ,label:"Enrollment"}]
    const batches=[{value:1, label:"Automibile"},{value:2 ,label:"Ac repairing"},
                    {value:3, label:"Full stack"},{value:4 ,label:"Cyber security"}]
  
    const [selectedOption, setSelectedOption] = useState("")
    const [selectedBatches, setselectedBatches] = useState("")
    const [items, setItems] = useState([]);
    // Reading excel file

    const readExcel = (file) => {
      const promise = new Promise((resolve, reject) => {
        const fileReader = new FileReader();
        fileReader.readAsArrayBuffer(file);
  
        fileReader.onload = (e) => {
          const bufferArray = e.target.result;
  
          const wb = XLSX.read(bufferArray, { type: "buffer" });
  
          const wsname = wb.SheetNames[0];
  
          const ws = wb.Sheets[wsname];
  
          const data = XLSX.utils.sheet_to_json(ws);
  
          resolve(data);
        };
  
        fileReader.onerror = (error) => {
          reject(error);
        };
      });
  
      promise.then((d) => {
        setItems(d);
      });
    };

    const[error,setserros]= useState("")
    // For handling the dropdown
    const handleChange=((e)=>{
      // const data = e.target.value
      // setSelectedOption(data)
      // console.log(selectedOption)
      setSelectedOption(e);
    })

    // for handling batches changes
   const  batchesChanges= ((e)=>{
      setselectedBatches(e)
      console.log("Batchec : ",e)
      // console.log(selectedBatches.e)
    })
    // handlingError((e)=>{
    //   if(setSelectedOption===""){
    //     setserros="Plase select Some option"
    //   }
    // })
        return(
            <Grid>
             <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Options</InputLabel>
            <SingleSelect
              name="" id=""
              // key={this.state.Selectedstate || ''}
              options={option}
              value={selectedOption}
              onChange={handleChange}
              // error={handlingError}
              // onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              // helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              // error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>
        
          <br/>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >batches</InputLabel>
            <SingleSelect
              // key={this.state.Selectedstate || ''}
              options={batches}
              value={selectedBatches}
              onChange={batchesChanges}
              // onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              // helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              // error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>

            <Input
                 name="upload-photo"
                 type="file"
                 onChange={(e) => {
                  const file = e.target.files[0];
                  readExcel(file);
                }}
                //  disabled={true}
            />
            </Grid>
       
        )
}
