import React, { Component } from 'react';
import * as XLSX from "xlsx";
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import { SingleSelect } from "react-select-material-ui";
import label from 'material-ui/svg-icons/action/label';
import { useState} from "react";
import { useHistory } from "react-router-dom";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import {DropzoneArea} from 'material-ui-dropzone';
import UserContext from '../components/GolbalContext'
import {fetchNotCompletedBatchDetails,saveBulkMetaData,saveBulkStudentData} from '../util/api';
import excel from 'xlsx';
let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
today = yyyy + '-' + mm + '-' + dd;

export default class bulkUpload extends Component{
  constructor(props){
    super(props)
    this.state={
      option:[{value:"Mobilization", label:"Mobilization"},
            , {value:"Enrollment" ,label:"Enrollment"}],
      batchList:[],
      Selectedoption:"",


    }
  }
  render(){
    return(
      <div style={{ width: '100%' }}>
      <Grid container>
      <Grid item xs={12}> </Grid>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Options</InputLabel>
            <SingleSelect
              name="Selectedoption" id="Selectedoption"
              options={this.state.options}
              value={this.state.Selectedoption || ''}
              key={this.state.Selectedoption || ''}
              onChange={this.handleOptionsChange.bind(this, 'Selectedoption')}
              // helperText={this.state.errors.Selectedoption != undefined ? this.state.errors.Selectedoption.label : ''}
              // error={this.state.errors.Selectedoption != undefined ? this.state.errors.Selectedoption.value : ''}
            />
          </Grid>

          <Grid item xs={12} sm={2}></Grid>

          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >State</InputLabel>
            <SingleSelect
              name="Selectedstate" id="Selectedstate"
              key={this.state.Selectedstate || ''}
              options={this.state.statelist}
              value={this.state.Selectedstate || ''}
              onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>
        </Grid>

        <br />
      </div>
    )
  }
}
