import React, { Component } from 'react';
import { Checkbox, InputLabel, Input, Grid, Button } from '@material-ui/core';
import { fetchAllStateDetails, fetchCenterByStateName, fetchEmployerDetails, fetchBatchDetails, fetchEnrollmentDetailsByBatchId, getBatchDetailsByBatchIdandFunderId, saveBatchFunderDetails, updateBatchFunderDetails } from './../util/api';
import UserContext from '../components/GolbalContext'
import { SingleSelect } from "react-select-material-ui";
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableHead from '@material-ui/core/TableHead';
import TableRow from '@material-ui/core/TableRow';
import MUIDataTable from "mui-datatables";
import EditIcon from '@material-ui/icons/Edit';
import { Link } from 'react-router-dom';
import Typography from '@material-ui/core/Typography';
import Card from '@material-ui/core/Card';
import Box from '@material-ui/core/Box';
import { data } from 'jquery';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import { Save } from '@material-ui/icons';

let today = new Date();
let dd = String(today.getDate()).padStart(2, '0');
let mm = String(today.getMonth() + 1).padStart(2, '0'); //January is 0!
let yyyy = today.getFullYear();
let hours = String(today.getHours()).padStart(2, '0');
let minutes = String(today.getMinutes()).padStart(2, '0');
let seconds = String(today.getSeconds()).padStart(2, '0');
today = yyyy + '-' + mm + '-' + dd + ' ' + hours + ':' + minutes + ':' + seconds;
//columns for the table

export default class FundManeger extends Component {
  constructor(props) {
    super(props)
    this.state = {
      regions: [{ value: '1', label: 'All' },
      { value: '2', label: 'East' },
      { value: '3', label: 'West' },
      { value: '4', label: 'North' },
      { value: '5', label: 'South' }],
      statelist: [],
      centerlist: [],
      centerId: "",
      funderId: "",
      funderType: "P",
      funderName: "",
      accountStatus: "Active Partner",
      typeOfRelationship: "%Funding partner%",
      funderlist: [],
      errors: {},
      tableData: [],
      mappedTableData: [],
      unmappedTableData: [],
      centerName: '',
      // Table data
      rowsSelected:''
    };
    this.arrayTableData = this.arrayTableData.bind(this);
    this.sepratingTableData = this.sepratingTableData.bind(this);
    // this.mapAllFunction = this.mapAllFunction.bind(this);
    // this.unMapAllFunction = this.unMapAllFunction.bind(this);
    //  this.handleRadioChange = this.handleRadioChange.bind(this);
  }

  // Module drop down for regions
  handleRegionsChange(selectname, event) {
    this.setState({
      [selectname]: event,
      errors: {
        ...this.state.errors,
        regions: {
          'label': '',
          'value': false
        },
        statelist: {
          'label': 'Please select some option',
          'value': true
        },
        centerId: {
          'label': 'Please select some option',
          'value': true
        },
        funderId: {
          'label': 'Please select some option',
          'value': true
        }
      }
    })
    fetchAllStateDetails().then(jsondata => {
      let result = JSON.parse(jsondata.data)
      result.forEach(element => {
        this.state.statelist.push({
          "value": element.name,
          "label": element.name
        })
      });
    })
  }



  // drop down for state
  handleStateChange(selectname, event) {
    this.setState({
      [selectname]: event,
    })
    fetchCenterByStateName(event).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      result.forEach(element => {
        this.state.centerlist.push({
          "value": element.id,
          "label": element.name,
        })
      })
    })
  }

  // drop down for center name
  handleCenterChange(selectname, event) {
    this.setState({
      [selectname]: event,
    })
    // Employer api calling to fetch funderlist
    fetchEmployerDetails(this.state.accountStatus, this.state.typeOfRelationship).then(jsondata => {
      let result = JSON.parse(jsondata.data)
      console.log(result)
      result.forEach(element => {
        this.state.funderlist.push({
          "value": element.id,
          "label": element.accountName
        })
      })
    })

  }

  // drop down for funder list
  handleFunderChange(selectname, event) {
    let funderId = event;
    let index = this.state.funderlist.findIndex((object)=> object.value == funderId)
    let funderName = this.state.funderlist[index].label
    this.setState({
      [selectname]: event,
      funderName
    })
    this.arrayTableData(funderId, funderName)   
  }

  handleRadioChange(event){
    let value = event.target.value
       this.setState({ funderType : value });
  }

  // Center id is static i.e 20 for a time being
  arrayTableData(funderId, funderName) {
    var tableData = []
    fetchBatchDetails("20").then(jsondata => {
      let result = JSON.parse(jsondata.data)
      var arrData = {}
      result.map((element, id) => {
        arrData = element
        tableData = [
          ...tableData,
          {
            "BatchName": arrData.batchName,
            "batchId": arrData.batchId,
            "funderName": funderName,
            "startDate": arrData.startDate,
            "endDate": arrData.endDate,
            "mapped": false,
            "funderId": funderId,
            "trainees": 0,
            "id": "",
            "isActive": "Y",
            "fundingType": this.state.funderType,
            "remarks": "",
          }]
      })
      this.sepratingTableData(tableData);
    })
    console.log("Seperating table data",this.state)
  }
  sepratingTableData(tableData){
    console.log("Sperating Table Data begins")
    this.setState({ mappedTableData: [], unmappedTableData: [] })
    var mappedTableData=[];
    var unmappedTableData=[];
    tableData.map((element, id) =>{
      //to get the student Strength present based on the enrollment ID's 
      fetchEnrollmentDetailsByBatchId(element.batchId).then((jsondata) => {
        let res = JSON.parse(jsondata.data)
        tableData[id].trainees = res.length
      })
      getBatchDetailsByBatchIdandFunderId("4567", "1234", "Y", this.state.funderType).then((jsondata) => {
        let res = JSON.parse(jsondata.data)     
        if (res[0].length > 0) {
          console.log("mapped table data")
          tableData[id].id = res[0][0].id
          tableData[id].isActive = res[0][0].isActive
          tableData[id].fundingType = res[0][0].fundingType
          tableData[id].remarks = res[0][0].remarks
          tableData[id].mapped = true
          tableData[id].action =<Button onClick={this.state.saveFunderMappingDetails}> <Save /></Button>
          mappedTableData = [
            ...mappedTableData, 
            tableData[id]
          ] 
        }
        else{
          unmappedTableData = [
            ...unmappedTableData,
            tableData[id]
          ]
        } 
        this.setState({unmappedTableData, mappedTableData})
      })
    })
  }

  //save batch funder mapping 
  saveFunderMappingDetails(batchId, funderId, isActive, fundingType, createdBy, createdOn, remarks) {
    alert("Data Saved")
    saveBatchFunderDetails(batchId, funderId, isActive, fundingType, createdBy, createdOn, remarks).then((jsondata) => {
      let res = JSON.parse(jsondata.data)
      console.log(res)
    })
  }

  //update batch funder mapping 
  updateFunderMappingDetails(id, batchId, funderId, isActive, remarks, updatedOn, updatedBy, fundingType) {
    updateBatchFunderDetails(id, batchId, funderId, isActive, remarks, updatedOn, updatedBy, fundingType).then((jsondata) => {
      let res = JSON.parse(jsondata.data)
      console.log(res)
    })
  }

  render() {
    const columns = [{label: 'Funder Name', name: 'funderName',headerStyle: {color:'#FF9800'}}
, {label: 'Batch Name', name: 'BatchName',headerStyle: {color:'#FF9800'}}
, {label: 'Start Date', name: 'startDate',headerStyle: {color:'#FF9800'}}
, {label: 'End Date', name: 'endDate',headerStyle: {color:'#FF9800'}}
, {label: 'No of Trainees', name: 'trainees',headerStyle: {color:'#FF9800'}} 
, {label: 'Action', name: 'action',headerStyle: {color:'#FF9800'}}
]

//options for the table

const options  = {
  
    selectableRows : "single",
    filterType: "dropdown",
    responsive: "stacked",
    sortDirection: "desc",
    disableToolbarSelect:true,
    rowsPerPage:10,
    selectableRowsOnClick: true,

     rowsSelected : this.state.selectedIndex,
    // onRowsSelect: (allRows) => {
    //     let rows = this.state.selectedIndex;
    //     rows[0] = allRows[0].dataIndex;
    //     this.setState({
    //       rowsSelected : this.state.mappedTableData[allRows[0].dataIndex],
    //       selectedIndex : rows,
    //       name:this.state.mappedTableData[allRows[0].dataIndex].name
    //     })
    //   }
};

    return (
      <div style={{ width: '100%' }}>
        <Grid container>
          <Grid item xs={12}> </Grid>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Regions</InputLabel>
            <SingleSelect
              name="Selectedregion" id="Selectedregion"
              options={this.state.regions}
              value={this.state.Selectedregion || ''}
              key={this.state.Selectedregion || ''}
              onChange={this.handleRegionsChange.bind(this, 'Selectedregion')}
              // helperText={this.state.errors.centerId != undefined ? this.state.errors.centerId.label : ''}
              error={this.state.errors.Selectedregion != undefined ? this.state.errors.centerId.value : ''}
            />
          </Grid>

          <Grid item xs={12} sm={2}></Grid>


          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >State</InputLabel>
            <SingleSelect
              name="Selectedstate" id="Selectedstate"
              key={this.state.Selectedstate || ''}
              options={this.state.statelist}
              value={this.state.Selectedstate || ''}
              onChange={this.handleStateChange.bind(this, 'Selectedstate')}
              // helperText={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.label : ''}
              error={this.state.errors.Selectedstate != undefined ? this.state.errors.Selectedstate.value : ''}
            />
          </Grid>
        </Grid>
        <br />
        <Grid container>
          <Grid item xs={12}> </Grid>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Center Name</InputLabel>
            <SingleSelect
              name="selectedcenter" id="centerId"
              options={this.state.centerlist}
              value={this.state.centerId || ''}
              onChange={this.handleCenterChange.bind(this, 'centerId')}
              // helperText={this.state.errors.centerId != undefined ? this.state.errors.batchId.centerId : ''}
              error={this.state.errors.centerId != undefined ? this.state.errors.centerId.value : ''}
              key={this.state.centerId || ''}
            />
          </Grid>

          <Grid item xs={12} sm={2}></Grid>
          <Grid item xs={12} sm={4}>
            <InputLabel shrink={true} >Funding Partner</InputLabel>
            <SingleSelect
              name="selectedfunder" id="funderId"
              options={this.state.funderlist}
              value={this.state.funderId || ''}
              onChange={this.handleFunderChange.bind(this, 'funderId')}
              // helperText={this.state.errors.funderId != undefined ? this.state.errors.funderId.label : ''}
              error={this.state.errors.funderId != undefined ? this.state.errors.funderId.value : ''}
            // key={this.state.funderId || ''}
            />
          </Grid>

        </Grid>
        <br />
        <br />
        <InputLabel>Funding Type</InputLabel>
        <br />
        <div onChange={this.handleRadioChange.bind(this)}>
          <input type="radio" value="P" name="funderType" checked={this.state.funderType === "P"}/> Primary
          &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
          <input type="radio" value="S" name="funderType" checked={this.state.funderType === "S"}/> Secondary
        </div>
        <br /><br /><br /><br /><br /><br /><br /><br />
        
        <MUIDataTable 

          title={"Mapped Batches"} 
          label={"Batches"}
          data={this.state.mappedTableData} 
          columns={columns} 
          options={options} 
          // onRowClick={this.state.saveFunderMappingDetails}
          // checkboxSelection={this.state.saveFunderMappingDetails}
        />
        <br /><br /><br /><br /><br /><br />

        <MUIDataTable
          title={"UnMapped Batches"} 
          label={"Batches"}
          data={this.state.unmappedTableData} 
          columns={columns} 
          options={options} 
          // onRowClick={this.state.saveFunderMappingDetails}
        />
  
    </div>
    )
  }
}
